{
    "apiVersion": "paas.envmgmt.io/v1",
    "kind": "ComputeInstance",
    "metadata": {
        "workspace": "default-ws",
        "project": "development",
        "labels": {
            "External-ID": "6c9da2c6-adb1-4a1b-90b3-2fde7d5632aa"
        },
        "name": "dev-vm-2"
    },
    "spec": {
        "computeProfile": {
            "name": "small-gpu-vm",
            "systemCatalog": true
        },
        "variables": [
            {
                "name": "cpu",
                "valueType": "text",
                "value": "24",
                "options": {
                    "override": {
                        "type": "allowed"
                    },
                    "displayMetadata": {
                        "alias": "CPU",
                        "disableForDay2Operation": false,
                        "disabled": true,
                        "section": "VM Specification",
                        "weight": "3"
                    }
                }
            },
            {
                "name": "disk",
                "valueType": "text",
                "value": "150",
                "options": {
                    "override": {
                        "type": "allowed"
                    },
                    "displayMetadata": {
                        "alias": "Disk",
                        "incrementDecrementLabel": "Disk Size (GB)",
                        "incrementDecrementMax": "2000",
                        "incrementDecrementMin": "100",
                        "incrementDecrementStep": "50",
                        "section": "VM Specification",
                        "type": "incrementDecrement",
                        "weight": "5"
                    }
                }
            },
            {
                "name": "gpu",
                "valueType": "text",
                "value": "1",
                "options": {
                    "override": {
                        "type": "allowed"
                    },
                    "displayMetadata": {
                        "alias": "GPU",
                        "disableForDay2Operation": false,
                        "disabled": true,
                        "radioOptions": [
                            {
                                "description": "",
                                "label": "1",
                                "value": "1"
                            }
                        ],
                        "section": "VM Specification",
                        "type": "text",
                        "weight": "2"
                    }
                }
            },
            {
                "name": "gpu-model",
                "valueType": "text",
                "value": "H100",
                "options": {
                    "override": {
                        "type": "allowed"
                    },
                    "displayMetadata": {
                        "alias": "GPU Type",
                        "disableForDay2Operation": false,
                        "disabled": true,
                        "dropdownValues": [
                            {
                                "label": "H100",
                                "text": "",
                                "value": "H100"
                            }
                        ],
                        "radioOptions": [
                            {
                                "description": "",
                                "label": "H100",
                                "value": "H100"
                            }
                        ],
                        "section": "VM Specification",
                        "type": "radio",
                        "weight": "1"
                    }
                }
            },
            {
                "name": "memory",
                "valueType": "text",
                "value": "256",
                "options": {
                    "override": {
                        "type": "allowed"
                    },
                    "displayMetadata": {
                        "alias": "Memory",
                        "disableForDay2Operation": false,
                        "disabled": true,
                        "section": "VM Specification",
                        "weight": "4"
                    }
                }
            },
            {
                "name": "password",
                "valueType": "text",
                "options": {
                    "override": {
                        "type": "allowed"
                    },
                    "displayMetadata": {
                        "alias": "VM Password",
                        "extraDescription": "VM Username: ubuntu",
                        "section": "VM Access",
                        "type": "text",
                        "weight": "6"
                    }
                },
                "value": "changeplz"
            },
            {
                "name": "ssh-key",
                "valueType": "text",
                "options": {
                    "override": {
                        "type": "allowed"
                    },
                    "displayMetadata": {
                        "alias": "SSH Public Key",
                        "section": "VM Access",
                        "weight": "6"
                    }
                },
                "value": ""
            },
            {
                "name": "video-memory",
                "valueType": "text",
                "value": "80",
                "options": {
                    "override": {
                        "type": "allowed"
                    },
                    "displayMetadata": {
                        "alias": "GPU Memory",
                        "disableForDay2Operation": false,
                        "disabled": true,
                        "section": "VM Specification",
                        "weight": "2.5"
                    }
                }
            }
        ],
        "computeType": "vm",
        "datacenter": {},
        "storageDetails": {}
    }
}

